<script>
// Function to open cash modal with dynamic total
function openCashModal() {
    if (typeof cart === 'undefined' || cart.length === 0) {
        alert('Cart is empty. Please add items before selecting payment method.');
        return;
    }
    
    // Check if customer is selected
    const customerSelect = document.getElementById('customerSelect');
    if (!customerSelect || !customerSelect.value) {
        alert('Please select a customer before selecting payment method.');
        if (customerSelect) {
            customerSelect.focus();
        }
        return;
    }
    
    // Get cart total
    const total = typeof getCartTotal === 'function' ? getCartTotal() : 0;
    
    // Update modal fields with cart total
    const payingAmountField = document.getElementById('cash-paying');
    if (payingAmountField) {
        payingAmountField.value = total.toFixed(2);
    }
    
    // Clear received amount and change
    const receivedAmountField = document.getElementById('cash-received');
    const changeField = document.getElementById('cash-change');
    if (receivedAmountField) receivedAmountField.value = '';
    if (changeField) changeField.value = '0.00';
    
    // Show the modal
    const modal = new bootstrap.Modal(document.getElementById('payment-cash'));
    modal.show();
}

// Function to calculate change when received amount changes
function calculateChange() {
    const payingAmount = parseFloat(document.getElementById('cash-paying').value) || 0;
    const receivedAmount = parseFloat(document.getElementById('cash-received').value) || 0;
    const change = receivedAmount - payingAmount;
    
    const changeField = document.getElementById('cash-change');
    if (changeField) {
        changeField.value = Math.max(0, change).toFixed(2);
    }
}

// Function to set quick cash amounts
function setQuickCash(amount) {
    const receivedAmountField = document.getElementById('cash-received');
    if (receivedAmountField) {
        receivedAmountField.value = amount.toFixed(2);
        calculateChange();
    }
}

// Function to validate and place order
function validateAndPlaceOrder(paymentMethod) {

    let isValid = true;
    let errorMessage = '';
    
    // Validate based on payment method
    switch(paymentMethod) {
        case 'cash':
            const cashReceived = parseFloat(document.getElementById('cash-received').value) || 0;
            
            const total = getCartTotal();
            if (cashReceived < total) {
                isValid = false;
                errorMessage = 'Cash received must be greater than or equal to total amount';
            }
            break;
            
        case 'card':
//            alert('Card payment validation started');
//            const cardNumber = document.getElementById('card-number')?.value.trim() || '';
//            const cardExpiry = document.getElementById('card-expiry')?.value.trim() || '';
//            const cardCvv = document.getElementById('card-cvv')?.value.trim() || '';
//            
//            console.log('Card details:', {cardNumber, cardExpiry, cardCvv});
//            
//            if (!cardNumber || cardNumber.replace(/\s/g, '').length < 13) {
//                isValid = false;
//                errorMessage = 'Please enter a valid card number';
//            } else if (!cardExpiry || cardExpiry.length < 5) {
//                isValid = false;
//                errorMessage = 'Please enter a valid expiry date';
//            } else if (!cardCvv || cardCvv.length < 3) {
//                isValid = false;
//                errorMessage = 'Please enter a valid CVV';
//            }
            break;
    }
    
    if (isValid) {
        // Close the payment modal
        const modalElement = document.querySelector('.modal.show');
        if (modalElement) {
            const modal = bootstrap.Modal.getInstance(modalElement);
            if (modal) {
                modal.hide();
            }
        }
        
        // Place the order
        placeOrder(paymentMethod);
    } else {
        alert(errorMessage);
    }
}

function placeOrder(paymentMethod) {
    console.log('Order placed with payment method:', paymentMethod);
    alert('Order placed successfully!');
}
</script>